contract MintBurnOwnableToken is ERC20, Ownable {

    bool public isMintable;
    bool public isBurnable;

    uint8 private decimals_;

    enum Type { Fixedd, Unlimited }

    constructor(string memory _name, 
                string memory _symbol,
                uint8 _decimals,
                uint256 _totalSupply,
                bool _isMintable,
                bool _isBurnable,
                Type _type
    ) ERC20(_name, _symbol) {

        isMintable = _isMintable;
        isBurnable = _isBurnable;

        if(_type == Type.Fixedd) {
            isMintable = false;
            isBurnable = false;
        }

        else if(_type == Type.Unlimited) {
            isMintable = true;
        }

        _mint(msg.sender, _totalSupply * (10 ** uint256(_decimals)));

        decimals_ = _decimals;

    }

    function decimals() public view override returns (uint8) {
        return decimals_;
    }

    function mint(address to, uint256 amount) public onlyOwner{
        require(isMintable, "Not Mintable");
        _mint(to, amount* (10 ** uint256(decimals_)));
    }

     function burn(uint256 amount) public onlyOwner {
        require(isBurnable, "Not Burnable");
        _burn(msg.sender, amount);
    }
}

