contract MintBurnRoleBasedToken is ERC20, AccessControlEnumerable {

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    bool public isMintable;
    bool public isBurnable;

    uint8 private decimals_;

    enum Type { Fixedd, Unlimited }

    constructor(string memory _name, 
                string memory _symbol,
                uint8 _decimals,
                uint256 _totalSupply,
                bool _isMintable,
                bool _isBurnable,
                Type _type
    ) ERC20(_name, _symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());

        isMintable = _isMintable;
        isBurnable = _isBurnable;

        if(_type == Type.Fixedd) {
            isMintable = false;
            isBurnable = false;
        }

        else if(_type == Type.Unlimited) {
            isMintable = true;
        }

        _mint(msg.sender, _totalSupply * (10 ** uint256(_decimals)));

        decimals_ = _decimals;

    }

    function decimals() public view override returns (uint8) {
        return decimals_;
    }

    function mint(address to, uint256 amount) public {
        require(isMintable, "Not Mintable");
        require(hasRole(MINTER_ROLE, _msgSender()), "ERC20PresetMinterPauser: must have minter role to mint");
        _mint(to, amount* (10 ** uint256(decimals_)));
    }

    function burn(uint256 amount) public {
        require(isBurnable, "Not Burnable");
        require(hasRole(MINTER_ROLE, _msgSender()), "ERC20PresetMinterPauser: must have minter role to burn");
        _burn(msg.sender, amount);
    }
}

